mapper.select:
SELECT
    incoming.create_time,incoming.update_time,product.name AS ProductName,
    partner.name AS PartnerName,product.settlement_mode,product.settlement_price,
    incoming.new_user,incoming.old_user,
    product.id as productId
FROM dc_product product LEFT JOIN dc_partner partner ON partner.id=product.partner_id
LEFT JOIN dc_product_incoming_config incoming ON product.id=incoming.product_id


    product.id=#{productId}


    and partner.id=#{partnerId}


    and incoming.create_time  >=   str_to_date( #{startTime}, '%Y-%m-%d %H:%I:%S');


    and incoming.update_time  <   str_to_date( #{endTime}, '%Y-%m-%d %H:%I:%S');
