select par.name, count(pro.partner_id) count, sum(ef.fee) cost from dc_partner par left join dc_product pro on par.id = pro.partner_id left join dc_event_fee ef on ef.product_id = pro.id WHERE
and partner_id = #{partnerId,jdbcType=INTEGER}
and ef.fee_status = 1
LIMIT #{page.startRowNum, jdbcType=INTEGER}, #{page.size, jdbcType=INTEGER}

select count(*) from dc_partner par left join dc_product pro on par.id = pro.partner_id left join dc_event_fee ef on ef.product_id = pro.id WHERE
and partner_id = #{partnerId,jdbcType=INTEGER}
and ef.fee_status = 1

select
f.id,u.phone,f.fee,f.fee_status as feeStatus,f.create_time as createTime
from dc_event_fee f
left join dc_user_events e on f.event_id = e.id
left join dc_user u on e.user_id = u.id
where f.status = 1


        and f.id = #{option.id,jdbcType=INTEGER}


        and e.product_id = #{option.productId,jdbcType=INTEGER}


        and f.create_time >= #{option.startDate,jdbcType=VARCHAR}


        and f.create_time <= #{option.endDate,jdbcType=INTEGER}


order by f.create_time desc
