        and partner_id = #{partnerId,jdbcType=INTEGER}


        and product_id = #{productId,jdbcType=INTEGER}


        and fee_type = #{feeType,jdbcType=TINYINT}


        and coope_mode = #{coopeMode,jdbcType=TINYINT}


        and event_type = #{eventType,jdbcType=TINYINT}


        and fee_status = #{feeStatus,jdbcType=TINYINT}


