select distinct(product_id) from dc_event_fee
where


        and partner_id = #{partnerId,jdbcType=INTEGER}


        and fee_type = #{feeType,jdbcType=TINYINT}


        and coope_mode = #{coopeMode,jdbcType=TINYINT}


        and event_type = #{eventType,jdbcType=TINYINT}


        and fee_status = #{feeStatus,jdbcType=TINYINT}


        and status = #{status,jdbcType=TINYINT}

select date(create_time) date, count(*) count
from dc_event_fee
where


        and partner_id = #{partnerId,jdbcType=INTEGER}


        and product_id = #{productId,jdbcType=INTEGER}


        and fee_type = #{feeType,jdbcType=TINYINT}


        and coope_mode = #{coopeMode,jdbcType=TINYINT}


        and event_type in (1, 2, 8)


        and fee_status = #{feeStatus,jdbcType=TINYINT}


        and status = #{status,jdbcType=TINYINT}


        and date(create_time) >= str_to_date(#{startDate}, '%Y-%m-%d %H:%i:%s')


        and date(create_time) <= str_to_date(#{endDate},'%Y-%m-%d %H:%i:%s')


group by year(create_time), month(create_time), day(create_time)
order by create_time desc
LIMIT
#{page.startRowNum, jdbcType=INTEGER}, #{page.size, jdbcType=INTEGER}

