select par.name, count(pro.partner_id) count, sum(ef.fee) cost from dc_partner par left join dc_product pro on par.id = pro.partner_id left join dc_event_fee ef on ef.product_id = pro.id WHERE
and partner_id = #{partnerId,jdbcType=INTEGER}
and ef.fee_status = 1
LIMIT #{page.startRowNum, jdbcType=INTEGER}, #{page.size, jdbcType=INTEGER}

