select
*
from dc_partner_account_log
where


        and partner_id = #{partnerId,jdbcType=INTEGER}


        and type = #{type,jdbcType=TINYINT}

    and status = 1

order by create_time desc
LIMIT
#{page.startRowNum, jdbcType=INTEGER}, #{page.size, jdbcType=INTEGER}

